import React, { useState } from 'react';
import { uploadFiles } from '../api/api';
import './FileUpload.css';

function FileUpload({ onHistoryMatch, getSimilarityColor }) {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);
  const [checkGoogle, setCheckGoogle] = useState(false);
  const [checkAI, setCheckAI] = useState(false);
  const [checkHistory, setCheckHistory] = useState(true);
  const [loading, setLoading] = useState(false);
  const [checkingHistory, setCheckingHistory] = useState(false);
  const [dragActive1, setDragActive1] = useState(false);
  const [dragActive2, setDragActive2] = useState(false);
  const [result, setResult] = useState(null);

  const handleFile1Change = (e) => {
    const file = e.target.files[0];
    if (file) {
      validateAndSetFile(file, setFile1);
    }
  };

  const handleFile2Change = (e) => {
    const file = e.target.files[0];
    if (file) {
      validateAndSetFile(file, setFile2);
    }
  };

  const validateAndSetFile = (file, setter) => {
    const allowedTypes = [
      'application/pdf',
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
      'text/plain',
    ];

    if (!allowedTypes.includes(file.type)) {
      alert('Only PDF, DOCX, and TXT files are allowed');
      return;
    }

    if (file.size > 10 * 1024 * 1024) {
      alert('File size must be less than 10MB');
      return;
    }

    setter(file);
  };

  const handleDrag = (e, setDragActive) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop1 = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive1(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      validateAndSetFile(e.dataTransfer.files[0], setFile1);
    }
  };

  const handleDrop2 = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive2(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      validateAndSetFile(e.dataTransfer.files[0], setFile2);
    }
  };

  const checkFileAgainstHistory = async (file) => {
    if (!checkHistory) {
      return false;
    }

    setCheckingHistory(true);
    try {
      const text = await readFileContent(file);
      
      if (!text || text.length < 50) {
        return false;
      }
      
      const { searchInHistory } = require('../api/api');
      const response = await searchInHistory({
        text: text,
        min_similarity: 50.0
      });

      if (response.data.matches_found > 0) {
        if (onHistoryMatch) {
          onHistoryMatch(response.data);
        }
        return true;
      }
      return false;
    } catch (error) {
      console.error('Error checking file against history:', error);
      return false;
    } finally {
      setCheckingHistory(false);
    }
  };

  const readFileContent = async (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      
      reader.onload = async (e) => {
        try {
          const content = e.target.result;
          
          if (file.type === 'text/plain') {
            resolve(content);
          } else {
            const text = content.substring(0, 3000);
            resolve(text);
          }
        } catch (error) {
          console.error('Error reading file:', error);
          resolve('');
        }
      };

      reader.onerror = () => {
        console.error('FileReader error');
        resolve('');
      };
      
      reader.readAsText(file);
    });
  };

  const handleUpload = async () => {
    if (!file1 || !file2) {
      alert('Please select both files to compare');
      return;
    }

    setLoading(true);
    setResult(null);

    try {
      if (checkHistory) {
        await checkFileAgainstHistory(file1);
      }

      const formData = new FormData();
      formData.append('file1', file1);
      formData.append('file2', file2);
      formData.append('check_google', checkGoogle);
      formData.append('check_ai', checkAI);

      const response = await uploadFiles(formData);
      setResult(response.data);
    } catch (error) {
      alert('Error uploading files: ' + (error.response?.data?.detail || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFile1(null);
    setFile2(null);
    setResult(null);
    setCheckGoogle(false);
    setCheckAI(false);
    setCheckHistory(true);
  };

  const removeFile = (setter) => {
    setter(null);
  };

  const getFileIcon = (file) => {
    if (!file) return 'ðŸ“„';
    if (file.type === 'application/pdf') return 'ðŸ“•';
    if (file.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document')
      return 'ðŸ“˜';
    return 'ðŸ“„';
  };

  return (
    <div className="file-upload-container">
      <h2>Upload Files to Compare</h2>
      <p className="file-info">Supported formats: PDF, DOCX, TXT (Max 10MB each)</p>

      {!result ? (
        <>
          <div className="upload-sections">
            <div className="upload-section">
              <h3>File 1</h3>
              <div
                className={`drop-zone ${dragActive1 ? 'drag-active' : ''} ${file1 ? 'has-file' : ''}`}
                onDragEnter={(e) => handleDrag(e, setDragActive1)}
                onDragLeave={(e) => handleDrag(e, setDragActive1)}
                onDragOver={(e) => handleDrag(e, setDragActive1)}
                onDrop={handleDrop1}
              >
                {!file1 ? (
                  <>
                    <div className="drop-icon">ðŸ“¤</div>
                    <p>Drag & drop file here</p>
                    <p className="or-text">or</p>
                    <label htmlFor="file1" className="btn-secondary">
                      Browse Files
                    </label>
                    <input
                      type="file"
                      id="file1"
                      onChange={handleFile1Change}
                      accept=".pdf,.docx,.txt"
                      style={{ display: 'none' }}
                    />
                  </>
                ) : (
                  <div className="file-preview">
                    <div className="file-icon">{getFileIcon(file1)}</div>
                    <div className="file-details">
                      <p className="file-name">{file1.name}</p>
                      <p className="file-size">{(file1.size / 1024).toFixed(2)} KB</p>
                    </div>
                    <button onClick={() => removeFile(setFile1)} className="btn-remove">
                      âœ•
                    </button>
                  </div>
                )}
              </div>
            </div>

            <div className="upload-section">
              <h3>File 2</h3>
              <div
                className={`drop-zone ${dragActive2 ? 'drag-active' : ''} ${file2 ? 'has-file' : ''}`}
                onDragEnter={(e) => handleDrag(e, setDragActive2)}
                onDragLeave={(e) => handleDrag(e, setDragActive2)}
                onDragOver={(e) => handleDrag(e, setDragActive2)}
                onDrop={handleDrop2}
              >
                {!file2 ? (
                  <>
                    <div className="drop-icon">ðŸ“¤</div>
                    <p>Drag & drop file here</p>
                    <p className="or-text">or</p>
                    <label htmlFor="file2" className="btn-secondary">
                      Browse Files
                    </label>
                    <input
                      type="file"
                      id="file2"
                      onChange={handleFile2Change}
                      accept=".pdf,.docx,.txt"
                      style={{ display: 'none' }}
                    />
                  </>
                ) : (
                  <div className="file-preview">
                    <div className="file-icon">{getFileIcon(file2)}</div>
                    <div className="file-details">
                      <p className="file-name">{file2.name}</p>
                      <p className="file-size">{(file2.size / 1024).toFixed(2)} KB</p>
                    </div>
                    <button onClick={() => removeFile(setFile2)} className="btn-remove">
                      âœ•
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>

          <div className="upload-options">
            <label>
              <input
                type="checkbox"
                checked={checkGoogle}
                onChange={(e) => setCheckGoogle(e.target.checked)}
              />
              Check Google Similarity
            </label>
            <label>
              <input
                type="checkbox"
                checked={checkAI}
                onChange={(e) => setCheckAI(e.target.checked)}
              />
              Check AI Content
            </label>
            <label>
              <input
                type="checkbox"
                checked={checkHistory}
                onChange={(e) => setCheckHistory(e.target.checked)}
              />
              Check Against Past History
            </label>
          </div>

          <button
            onClick={handleUpload}
            className="btn-primary btn-upload"
            disabled={loading || checkingHistory || !file1 || !file2}
          >
            {checkingHistory ? 'Checking History...' : loading ? 'Processing...' : 'Compare Files'}
          </button>
        </>
      ) : (
        <>
          <div className="file-result-header">
            <h3>ðŸ“Š File Comparison Results</h3>
            <button onClick={handleReset} className="btn-secondary">
              Upload New Files
            </button>
          </div>

          <div className="file-names-display">
            <div className="file-name-item">
              <span className="file-label">File 1:</span>
              <span className="file-value">{file1?.name}</span>
            </div>
            <div className="file-name-item">
              <span className="file-label">File 2:</span>
              <span className="file-value">{file2?.name}</span>
            </div>
          </div>

          <div className="result-card">
            <div className="result-item">
              <span className="result-label">Text Similarity:</span>
              <span
                className="result-value"
                style={{ color: getSimilarityColor(result.similarity_score) }}
              >
                {result.similarity_score}%
              </span>
            </div>
            {result.google_similarity !== null && (
              <div className="result-item">
                <span className="result-label">Google Similarity:</span>
                <span
                  className="result-value"
                  style={{ color: getSimilarityColor(result.google_similarity) }}
                >
                  {result.google_similarity}%
                </span>
              </div>
            )}
            <div className="result-message">
              <strong>{result.message}</strong>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default FileUpload;
