import React, { useState } from 'react';
import { uploadFiles, searchInHistory } from '../api/api';
import HistoryMatches from './HistoryMatches';
import './FileUpload.css';

function FileUpload({ onResult }) {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);
  const [checkGoogle, setCheckGoogle] = useState(false);
  const [checkAI, setCheckAI] = useState(false);
  const [checkHistory, setCheckHistory] = useState(true);
  const [loading, setLoading] = useState(false);
  const [dragActive1, setDragActive1] = useState(false);
  const [dragActive2, setDragActive2] = useState(false);
  const [historyMatches, setHistoryMatches] = useState(null);
  const [showHistoryMatches, setShowHistoryMatches] = useState(false);
  const [checkingHistory, setCheckingHistory] = useState(false);
  const [extractedText1, setExtractedText1] = useState('');

  const handleFile1Change = (e) => {
    const file = e.target.files[0];
    if (file) {
      validateAndSetFile(file, setFile1);
    }
  };

  const handleFile2Change = (e) => {
    const file = e.target.files[0];
    if (file) {
      validateAndSetFile(file, setFile2);
    }
  };

  const validateAndSetFile = (file, setter) => {
    const allowedTypes = [
      'application/pdf',
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
      'text/plain',
    ];

    if (!allowedTypes.includes(file.type)) {
      alert('Only PDF, DOCX, and TXT files are allowed');
      return;
    }

    if (file.size > 10 * 1024 * 1024) {
      alert('File size must be less than 10MB');
      return;
    }

    setter(file);
  };

  const handleDrag = (e, setDragActive) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop1 = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive1(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      validateAndSetFile(e.dataTransfer.files[0], setFile1);
    }
  };

  const handleDrop2 = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive2(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      validateAndSetFile(e.dataTransfer.files[0], setFile2);
    }
  };

  // Extract text from TXT file
  const extractTextFromTxtFile = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => {
        const text = e.target.result;
        resolve(text);
      };
      reader.onerror = (error) => {
        console.error('Error reading file:', error);
        reject(error);
      };
      reader.readAsText(file);
    });
  };

  // Check file content against history
  const checkFileAgainstHistory = async (file) => {
    if (!checkHistory) {
      console.log('History check disabled');
      return false;
    }

    console.log('Starting history check for file:', file.name, 'Type:', file.type);
    setCheckingHistory(true);

    try {
      let textToCheck = '';

      // Extract text from TXT files
      if (file.type === 'text/plain') {
        console.log('Extracting text from TXT file...');
        textToCheck = await extractTextFromTxtFile(file);
        console.log('Extracted text length:', textToCheck.length);
        setExtractedText1(textToCheck);
      } else {
        // For PDF and DOCX, show info message
        console.log(`File type ${file.type} - will be checked after upload`);
        alert(`Note: ${file.name} will be compared against your history after processing. History check is available for TXT files before upload.`);
        setCheckingHistory(false);
        return false;
      }

      // Check if we have enough text
      if (!textToCheck || textToCheck.trim().length < 50) {
        console.log('Text too short for history check');
        alert('File content is too short for history checking (minimum 50 characters required).');
        setCheckingHistory(false);
        return false;
      }

      console.log('Checking against history...');
      
      // Search in history
      const response = await searchInHistory({
        text: textToCheck,
        min_similarity: 50.0
      });

      console.log('History search response:', response.data);

      if (response.data.matches_found > 0) {
        console.log(`Found ${response.data.matches_found} matches in history!`);
        setHistoryMatches(response.data);
        setShowHistoryMatches(true);
        
        // Show alert about matches
        alert(`âš ï¸ WARNING: Found ${response.data.matches_found} similar submission(s) in your history!\n\nHighest similarity: ${response.data.highest_similarity}%\n\nClick to view details in the popup.`);
        
        return true;
      } else {
        console.log('No matches found in history');
        alert('âœ… No similar content found in your history. This appears to be new content.');
      }

      return false;
    } catch (error) {
      console.error('Error checking history:', error);
      alert('Error checking history: ' + (error.response?.data?.detail || error.message));
      return false;
    } finally {
      setCheckingHistory(false);
    }
  };

  const handleUpload = async () => {
    if (!file1 || !file2) {
      alert('Please select both files to compare');
      return;
    }

    console.log('=== Starting File Upload Process ===');
    console.log('File 1:', file1.name, 'Type:', file1.type);
    console.log('File 2:', file2.name, 'Type:', file2.type);
    console.log('Check History:', checkHistory);
    console.log('Check Google:', checkGoogle);
    console.log('Check AI:', checkAI);

    setHistoryMatches(null);
    setShowHistoryMatches(false);

    try {
      // Step 1: Check file1 against history (only for TXT files)
      if (checkHistory) {
        console.log('Step 1: Checking file against history...');
        const historyMatchFound = await checkFileAgainstHistory(file1);
        
        if (historyMatchFound) {
          console.log('History matches found! Showing modal...');
          // User can review the modal and decide to continue or cancel
        }
      }

      // Step 2: Upload and process files
      console.log('Step 2: Uploading files to server...');
      setLoading(true);

      const formData = new FormData();
      formData.append('file1', file1);
      formData.append('file2', file2);
      formData.append('check_google', checkGoogle.toString());
      formData.append('check_ai', checkAI.toString());

      console.log('FormData created, sending to server...');

      const response = await uploadFiles(formData);
      
      console.log('Server response:', response.data);
      
      onResult(response.data);
      
      // Success message with details
      let successMessage = `âœ… Files processed successfully!\n\n`;
      successMessage += `File 1: ${file1.name}\n`;
      successMessage += `File 2: ${file2.name}\n`;
      successMessage += `Similarity: ${response.data.similarity_score}%\n`;
      
      if (response.data.google_similarity !== null) {
        successMessage += `Google Similarity: ${response.data.google_similarity}%\n`;
      }
      
      if (response.data.ai_detection) {
        successMessage += `AI Probability: ${response.data.ai_detection.ai_probability}%\n`;
      }

      alert(successMessage);

    } catch (error) {
      console.error('Upload error:', error);
      const errorMessage = error.response?.data?.detail || error.message;
      alert('âŒ Error uploading files: ' + errorMessage);
    } finally {
      setLoading(false);
      console.log('=== Upload Process Complete ===');
    }
  };

  const removeFile = (setter) => {
    setter(null);
  };

  const getFileIcon = (file) => {
    if (!file) return 'ðŸ“„';
    if (file.type === 'application/pdf') return 'ðŸ“•';
    if (file.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document')
      return 'ðŸ“˜';
    return 'ðŸ“„';
  };

  return (
    <div className="file-upload-container">
      <h2>Upload Files to Compare</h2>
      <p className="file-info">Supported formats: PDF, DOCX, TXT (Max 10MB each)</p>

      {checkHistory && (
        <div className="history-check-info">
          <p>ðŸ“‹ <strong>History Check Enabled:</strong></p>
          <ul>
            <li>TXT files: Checked against history BEFORE upload âš¡</li>
            <li>PDF/DOCX files: Stored in history AFTER processing for future checks ðŸ’¾</li>
            <li>You'll be notified if similar content is found! ðŸ””</li>
          </ul>
        </div>
      )}

      <div className="upload-sections">
        <div className="upload-section">
          <h3>File 1</h3>
          <div
            className={`drop-zone ${dragActive1 ? 'drag-active' : ''} ${file1 ? 'has-file' : ''}`}
            onDragEnter={(e) => handleDrag(e, setDragActive1)}
            onDragLeave={(e) => handleDrag(e, setDragActive1)}
            onDragOver={(e) => handleDrag(e, setDragActive1)}
            onDrop={handleDrop1}
          >
            {!file1 ? (
              <>
                <div className="drop-icon">ðŸ“¤</div>
                <p>Drag & drop file here</p>
                <p className="or-text">or</p>
                <label htmlFor="file1" className="btn-secondary">
                  Browse Files
                </label>
                <input
                  type="file"
                  id="file1"
                  onChange={handleFile1Change}
                  accept=".pdf,.docx,.txt"
                  style={{ display: 'none' }}
                />
              </>
            ) : (
              <div className="file-preview">
                <div className="file-icon">{getFileIcon(file1)}</div>
                <div className="file-details">
                  <p className="file-name">{file1.name}</p>
                  <p className="file-size">{(file1.size / 1024).toFixed(2)} KB</p>
                  <p className="file-type">{file1.type === 'text/plain' ? 'TXT' : file1.type === 'application/pdf' ? 'PDF' : 'DOCX'}</p>
                </div>
                <button onClick={() => removeFile(setFile1)} className="btn-remove">
                  âœ•
                </button>
              </div>
            )}
          </div>
        </div>

        <div className="upload-section">
          <h3>File 2</h3>
          <div
            className={`drop-zone ${dragActive2 ? 'drag-active' : ''} ${file2 ? 'has-file' : ''}`}
            onDragEnter={(e) => handleDrag(e, setDragActive2)}
            onDragLeave={(e) => handleDrag(e, setDragActive2)}
            onDragOver={(e) => handleDrag(e, setDragActive2)}
            onDrop={handleDrop2}
          >
            {!file2 ? (
              <>
                <div className="drop-icon">ðŸ“¤</div>
                <p>Drag & drop file here</p>
                <p className="or-text">or</p>
                <label htmlFor="file2" className="btn-secondary">
                  Browse Files
                </label>
                <input
                  type="file"
                  id="file2"
                  onChange={handleFile2Change}
                  accept=".pdf,.docx,.txt"
                  style={{ display: 'none' }}
                />
              </>
            ) : (
              <div className="file-preview">
                <div className="file-icon">{getFileIcon(file2)}</div>
                <div className="file-details">
                  <p className="file-name">{file2.name}</p>
                  <p className="file-size">{(file2.size / 1024).toFixed(2)} KB</p>
                  <p className="file-type">{file2.type === 'text/plain' ? 'TXT' : file2.type === 'application/pdf' ? 'PDF' : 'DOCX'}</p>
                </div>
                <button onClick={() => removeFile(setFile2)} className="btn-remove">
                  âœ•
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="upload-options">
        <label>
          <input
            type="checkbox"
            checked={checkGoogle}
            onChange={(e) => setCheckGoogle(e.target.checked)}
          />
          Check Google Similarity
        </label>
        <label>
          <input
            type="checkbox"
            checked={checkAI}
            onChange={(e) => setCheckAI(e.target.checked)}
          />
          Check AI Content
        </label>
        <label>
          <input
            type="checkbox"
            checked={checkHistory}
            onChange={(e) => setCheckHistory(e.target.checked)}
          />
          Check Against Past History
        </label>
      </div>

      <button
        onClick={handleUpload}
        className="btn-primary btn-upload"
        disabled={loading || !file1 || !file2 || checkingHistory}
      >
        {checkingHistory ? (
          <>
            <span className="spinner-small"></span>
            Checking History...
          </>
        ) : loading ? (
          <>
            <span className="spinner-small"></span>
            Processing Files...
          </>
        ) : (
          'ðŸ“Š Compare Files'
        )}
      </button>

      {/* History Matches Modal */}
      {showHistoryMatches && historyMatches && (
        <HistoryMatches
          matches={historyMatches}
          onClose={() => setShowHistoryMatches(false)}
        />
      )}
    </div>
  );
}

export default FileUpload;
